SELECT
	COUNT(*)
FROM rental JOIN member USING (mem_num) WHERE mem_num=#{mem_num}

SELECT * FROM(SELECT a.*, rownum rnum FROM (SELECT rental_num, scr_num, rental_name, rental_date, rental_regdate, rental_status FROM rental JOIN member USING(mem_num) WHERE mem_num=#{mem_num}
ORDER BY rental_regdate DESC)a)
WHERE rnum >= #{start} AND rnum <= #{end}

